(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[17974],{17974:n=>{n.exports='<button\n    tuiButton\n    type="button"\n    (click)="step.set(step() || 1)"\n>\n    Show popover\n</button>\n<div\n    *tuiPopup="!!step()"\n    appearance="floating"\n    tuiAnimated\n    tuiCardLarge="compact"\n    class="popover"\n>\n    @if (step() === 1) {\n        <h1 tuiTitle>\n            Leave your feedback\n            <span tuiSubtitle>It will only take 3 minutes</span>\n        </h1>\n        <tui-rating [(ngModel)]="rating" />\n    }\n    @if (step() === 2) {\n        <h1 tuiTitle>Why so?</h1>\n        <tui-textfield tuiTextfieldSize="m">\n            <textarea\n                placeholder="Leave a few words"\n                tuiAutoFocus\n                tuiTextarea\n                [max]="3"\n                [min]="3"\n                [(ngModel)]="comment"\n            ></textarea>\n        </tui-textfield>\n    }\n    <button\n        appearance="icon"\n        iconStart="@tui.x"\n        size="xs"\n        tuiIconButton\n        type="button"\n        class="close"\n        (click)="close()"\n    >\n        Close\n    </button>\n    <footer class="footer">\n        @if (step() === 2) {\n            <button\n                appearance="secondary"\n                size="s"\n                tuiButton\n                type="button"\n                (click)="step.set(1)"\n            >\n                Back\n            </button>\n        }\n        <button\n            appearance="primary"\n            size="s"\n            tuiButton\n            type="button"\n            (click)="step() === 1 ? step.set(2) : close()"\n        >\n            {{ step() === 1 ? \'Next\' : \'Submit\' }}\n        </button>\n    </footer>\n</div>\n'}}]);