(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[29286],{29286:e=>{e.exports='<tui-files>\n    @for (file of files; track file) {\n        <tui-file\n            state="normal"\n            [file]="file"\n            [showDelete]="control.enabled"\n        />\n    }\n\n    @for (file of rejectedFiles; track file) {\n        <tui-file\n            state="error"\n            [file]="file"\n            [showDelete]="control.enabled"\n        />\n    }\n\n    @if (loadingFile && !isE2E) {\n        <tui-file\n            state="loading"\n            [file]="loadingFile"\n            [showDelete]="control.enabled"\n            (remove)="removeLoading()"\n        />\n    }\n</tui-files>\n\n<h4>With link</h4>\n<tui-files>\n    <a\n        *tuiItem\n        rel="noreferrer"\n        state="normal"\n        target="_blank"\n        tuiFile\n        [file]="fileWithLink"\n        [href]="fileWithLink.src"\n        [showDelete]="control.enabled"\n    ></a>\n</tui-files>\n\n<h4>With deleted state</h4>\n<tui-files>\n    @for (file of removedFiles; track file) {\n        <tui-file\n            size="l"\n            state="deleted"\n            [file]="file"\n            [showDelete]="control.enabled"\n        >\n            <button\n                tuiLink\n                type="button"\n                (click)="restore(file)"\n            >\n                Restore\n            </button>\n        </tui-file>\n    }\n    @for (file of restoredFiles; track file) {\n        <tui-file\n            size="l"\n            state="normal"\n            [file]="file"\n            [leftContent]="icon"\n            [showDelete]="control.enabled"\n            (remove)="remove(file)"\n        />\n    }\n</tui-files>\n\n<ng-template #icon>\n    <tui-icon src="@tui.file" />\n</ng-template>\n'}}]);