(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[3216],{3216:n=>{n.exports='<label tuiInputFiles>\n    <input\n        tuiInputFiles\n        [formControl]="control"\n    />\n    <ng-template let-dragged>\n        @if (dragged) {\n            <div class="content">\n                <div\n                    appearance="secondary"\n                    tuiAvatar="@tui.droplet"\n                ></div>\n                <div>\n                    Drop it like it\'s hot!\n                    <br />\n                    <br />\n                </div>\n            </div>\n        } @else {\n            <div class="content">\n                <div\n                    appearance="secondary"\n                    tuiAvatar="@tui.cloud-upload"\n                ></div>\n                <div>\n                    Drag and drop file here or\n                    <a tuiLink>click to upload</a>\n                </div>\n            </div>\n        }\n    </ng-template>\n</label>\n\n<tui-files class="tui-space_top-4">\n    <tui-file\n        [file]="file"\n        [leftContent]="content"\n    >\n        <span class="tui-text_body-s-2">file is on checking</span>\n    </tui-file>\n</tui-files>\n\n<ng-template #content>\n    <tui-icon icon="@tui.clock" />\n</ng-template>\n'}}]);