(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[33261],{33261:n=>{n.exports='<ng-container tuiTableFilters>\n    <form [formGroup]="filterForm">\n        <tui-textfield>\n            <label tuiLabel>Minimal balance</label>\n            <input\n                formControlName="balance"\n                tuiInputNumber\n                tuiTableFilter="controls"\n                [tuiGenericFilter]="filter"\n            />\n        </tui-textfield>\n\n        <label class="toggle">\n            <input\n                size="s"\n                tuiSwitch\n                type="checkbox"\n                [ngModel]="filterForm.enabled"\n                [ngModelOptions]="{standalone: true}"\n                [showIcons]="false"\n                (ngModelChange)="onToggle($event)"\n            />\n            Enable filtering\n        </label>\n    </form>\n    <table\n        tuiTable\n        class="table"\n        [columns]="columns"\n    >\n        <thead>\n            <tr tuiThGroup>\n                <th tuiTh>Name</th>\n                <th tuiTh>Balance</th>\n            </tr>\n        </thead>\n        <tbody tuiTbody>\n            <tr\n                *ngFor="let item of items() | tuiTableFilters | async"\n                tuiTr\n                [formGroup]="item"\n            >\n                <td\n                    *tuiCell="\'name\'"\n                    tuiTd\n                >\n                    <tui-textfield>\n                        <input\n                            autocomplete="name"\n                            formControlName="name"\n                            placeholder="Type an name"\n                            tuiTextfield\n                        />\n                    </tui-textfield>\n                </td>\n                <td\n                    *tuiCell="\'balance\'"\n                    tuiTd\n                >\n                    <tui-textfield>\n                        <input\n                            formControlName="balance"\n                            placeholder="Type a balance"\n                            tuiInputNumber\n                        />\n                    </tui-textfield>\n                </td>\n            </tr>\n        </tbody>\n    </table>\n\n    <button\n        tuiButton\n        type="button"\n        class="tui-space_top-2"\n        (click)="addRow()"\n    >\n        Add\n    </button>\n</ng-container>\n'}}]);