(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[38255],{38255:n=>{n.exports='<label>\n    Type a name:\n\n    <tui-combo-box\n        *tuiLet="service.request(search) | async as filtered"\n        [formControl]="control"\n        [tuiTextfieldLabelOutside]="true"\n        [valueContent]="value"\n        [(search)]="search"\n    >\n        Find the best employees\n        <input\n            placeholder="Type a name"\n            tuiTextfieldLegacy\n        />\n        <ng-template\n            #value\n            let-item\n        >\n            <div\n                size="xs"\n                class="avatar"\n                [tuiAvatar]="item.avatarUrl"\n            >\n                {{ item.toString() | tuiInitials }}\n            </div>\n            <span class="name">{{ item }}</span>\n        </ng-template>\n        <ng-template tuiDataList>\n            @if (filtered) {\n                <tui-data-list>\n                    @for (item of filtered; track item) {\n                        <button\n                            tuiOption\n                            type="button"\n                            [disabled]="item.disabled"\n                            [value]="item"\n                        >\n                            <div\n                                size="xs"\n                                class="avatar"\n                                [tuiAvatar]="item.avatarUrl"\n                            >\n                                {{ item.toString() | tuiInitials }}\n                            </div>\n                            <span class="name">{{ item }}</span>\n                        </button>\n                    }\n                </tui-data-list>\n            } @else {\n                <tui-loader class="tui-space_vertical-4" />\n            }\n        </ng-template>\n    </tui-combo-box>\n</label>\n'}}]);