(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[38735],{38735:n=>{n.exports='<tui-textfield tuiChevron>\n    <input\n        placeholder="Select film"\n        tuiComboBox\n        [(ngModel)]="value"\n    />\n\n    <tui-data-list\n        *tuiDropdown\n        size="s"\n    >\n        <section class="filters">\n            @for (category of categories; track category) {\n                <label\n                    tuiChip\n                    [appearance]="filters[category] ? \'accent\' : \'neutral\'"\n                    (pointerdown.prevent)="(0)"\n                >\n                    {{ category }}\n                    <input\n                        hidden\n                        type="checkbox"\n                        [(ngModel)]="filters[category]"\n                    />\n                </label>\n            }\n        </section>\n\n        @for (group of filmDatabase | keyvalue; track group) {\n            @if (filters[group.key]) {\n                <tui-opt-group [label]="group.key">\n                    @for (film of group.value | tuiFilterByInput; track film) {\n                        <button\n                            new\n                            tuiOption\n                            type="button"\n                            [value]="film"\n                        >\n                            {{ film }}\n                        </button>\n                    }\n                </tui-opt-group>\n            }\n        }\n    </tui-data-list>\n</tui-textfield>\n'}}]);