(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[38880],{38880:n=>{n.exports='<h2>Scroll to see the floating</h2>\n\n<p>\n    <label tuiLabel>\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="floating"\n        />\n        Floating visibility\n    </label>\n</p>\n<p>\n    <label tuiLabel>\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="secondAction"\n        />\n        Second action visibility\n    </label>\n</p>\n\n<div\n    #content\n    class="content"\n    (scroll)="onScroll(content)"\n>\n    <div\n        *tuiRepeatTimes="let i of 30"\n        tuiCell\n    >\n        <tui-avatar\n            appearance="primary"\n            src="@tui.star"\n        />\n        <div tuiTitle>\n            Title\n            <div tuiSubtitle>Description</div>\n        </div>\n    </div>\n\n    @if (floating) {\n        <footer\n            @tuiSlideInTop\n            [tuiFloatingContainer]="secondAction ? \'\' : \'transparent\'"\n        >\n            <button\n                tuiButton\n                type="button"\n            >\n                Main action\n            </button>\n            @if (secondAction) {\n                <button\n                    appearance="flat"\n                    tuiButton\n                    type="button"\n                    @tuiHeightCollapse\n                >\n                    Secondary action\n                </button>\n            }\n        </footer>\n    }\n</div>\n'}}]);