(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[39647],{39647:n=>{n.exports='<ng-container *ngFor="let platform of platforms">\n    <h2>{{ platform === \'web\' ? \'Desktop\' : \'Mobile\' }}</h2>\n    <section [tuiPlatform]="platform">\n        <button\n            tuiToast\n            type="button"\n        >\n            Plain text interactive\n        </button>\n        <div tuiToast>\n            With action\n            <button\n                tuiButton\n                type="button"\n            >\n                Action\n            </button>\n        </div>\n    </section>\n    <section [tuiPlatform]="platform">\n        <div\n            iconStart="@tui.info"\n            tuiToast\n        >\n            With icon\n        </div>\n        <div tuiToast>\n            <tui-icon\n                appearance="accent"\n                icon="@tui.box"\n                tuiBadge\n            />\n            With badge\n            <button\n                appearance="icon"\n                iconStart="@tui.x"\n                size="xs"\n                tuiIconButton\n                type="button"\n            >\n                Close\n            </button>\n        </div>\n    </section>\n    <section [tuiPlatform]="platform">\n        <div tuiToast>\n            <tui-avatar [src]="\'https://github.com/waterplea.png\' | tuiFallbackSrc: \'@tui.user\' | async" />\n            Avatar\n        </div>\n        <div tuiToast>\n            <tui-avatar [src]="\'https://github.com/marsibarsi.png\' | tuiFallbackSrc: \'@tui.user\' | async" />\n            Everything\n            <button\n                tuiButton\n                type="button"\n            >\n                Action\n            </button>\n            <button\n                *ngIf="platform === \'web\'"\n                appearance="icon"\n                iconStart="@tui.x"\n                size="xs"\n                tuiIconButton\n                type="button"\n            >\n                Close\n            </button>\n        </div>\n    </section>\n    <section [tuiPlatform]="platform">\n        <div\n            iconStart="@tui.alarm-clock"\n            tuiToast\n        >\n            The text of the notification telling what happened is in three lines because there is a lot of information\n            <button\n                tuiButton\n                type="button"\n            >\n                Action\n            </button>\n        </div>\n    </section>\n</ng-container>\n'}}]);