(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[46994],{46994:n=>{n.exports='<p>Right-click any table row.</p>\n\n<table tuiTable>\n    <thead tuiTHead>\n        <tr tuiThGroup>\n            @for (column of tableColumns; track column) {\n                <th tuiTh>\n                    {{ column }}\n                </th>\n            }\n        </tr>\n    </thead>\n    <tbody tuiTbody>\n        @for (rowInfo of tableData; track rowInfo) {\n            <tr\n                #dropdown="tuiDropdown"\n                tuiDropdownContext\n                [tuiDropdown]="contextMenu"\n            >\n                @for (value of getObjectValues(rowInfo); track value) {\n                    <td tuiTd>\n                        {{ value }}\n                    </td>\n                }\n                <ng-template #contextMenu>\n                    <tui-data-list\n                        role="menu"\n                        tuiDataListDropdownManager\n                        class="context-menu"\n                    >\n                        @for (item of menuItems; track item) {\n                            <button\n                                tuiOption\n                                type="button"\n                                (click)="printToConsole(item.title, rowInfo); dropdown.toggle(false)"\n                            >\n                                {{ item.title }}\n                                <tui-icon\n                                    class="icon"\n                                    [icon]="item.iconName"\n                                />\n                            </button>\n                        }\n                        <button\n                            iconEnd="@tui.chevron-right"\n                            tuiDropdownAlign="right"\n                            tuiDropdownSided\n                            tuiOption\n                            type="button"\n                            [tuiDropdown]="nestedMenu"\n                        >\n                            More\n                        </button>\n                    </tui-data-list>\n                    <ng-template #nestedMenu>\n                        <tui-data-list>\n                            @for (option of moreOptions; track option) {\n                                <button\n                                    tuiOption\n                                    type="button"\n                                >\n                                    {{ option }}\n                                </button>\n                            }\n                        </tui-data-list>\n                    </ng-template>\n                </ng-template>\n            </tr>\n        }\n    </tbody>\n</table>\n'}}]);