(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[4906],{4906:n=>{n.exports='<p>\n    <label tuiLabel>\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="floating"\n        />\n        Floating visibility\n    </label>\n</p>\n\n<p>\n    <label tuiLabel>\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="additional"\n        />\n        Additional content\n    </label>\n</p>\n\n<div class="content">\n    @for (i of 30 | tuiRepeatTimes; track i) {\n        <div tuiCell>\n            <tui-avatar\n                appearance="primary"\n                src="@tui.star"\n            />\n            <div tuiTitle>\n                Title\n                <div tuiSubtitle>Description</div>\n            </div>\n        </div>\n    }\n\n    @if (floating) {\n        <footer\n            tuiFloatingContainer\n            @tuiSlideInTop\n        >\n            <tui-elastic-container>\n                <button\n                    tuiButton\n                    type="button"\n                >\n                    Main action\n                </button>\n                @if (!additional) {\n                    <span\n                        class="clamp"\n                        [@tuiCrossFade]="{bottom: \'1rem\'} | tuiAnimation"\n                    >\n                        <tui-icon\n                            icon="@tui.settings"\n                            [style.font-size.rem]="1"\n                        />\n                        Legal text, max 3 lines\n                    </span>\n                }\n                @if (additional) {\n                    <button\n                        appearance="flat"\n                        tuiButton\n                        type="button"\n                        [@tuiCrossFade]="{bottom: \'1rem\'} | tuiAnimation"\n                    >\n                        Secondary action\n                    </button>\n                }\n            </tui-elastic-container>\n        </footer>\n    }\n</div>\n'}}]);