(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[5150],{5150:n=>{n.exports='<form\n    tuiAppearance="floating"\n    tuiCardLarge\n    tuiForm="m"\n    [formGroup]="form"\n    [style.max-width.rem]="32"\n>\n    <header tuiHeader>\n        <h2 tuiTitle>\n            Registration form\n            <span tuiSubtitle>Tell us about yourself</span>\n        </h2>\n        <span tuiAccessories>\n            <tui-segmented>\n                <label>\n                    <input\n                        formControlName="basic"\n                        type="radio"\n                        [value]="true"\n                    />\n                    Brief\n                </label>\n                <label>\n                    <input\n                        formControlName="basic"\n                        type="radio"\n                        [value]="false"\n                    />\n                    Detailed\n                </label>\n            </tui-segmented>\n        </span>\n    </header>\n    <tui-notification appearance="warning">\n        <h3 tuiTitle>\n            Authenticity required\n            <span tuiSubtitle>Please be honest and use your real data</span>\n        </h3>\n    </tui-notification>\n    <tui-textfield>\n        <label tuiLabel>Name</label>\n        <input\n            formControlName="name"\n            placeholder="John Wick"\n            tuiTextfield\n        />\n    </tui-textfield>\n    <tui-error formControlName="name" />\n    @if (!form.controls.basic.value) {\n        <tui-textfield>\n            <label tuiLabel>Email</label>\n            <input\n                formControlName="email"\n                placeholder="john@wick.com"\n                tuiTextfield\n                type="email"\n            />\n        </tui-textfield>\n        <label tuiLabel>\n            <input\n                formControlName="subscribe"\n                tuiSwitch\n                type="checkbox"\n            />\n            Subscribe for newsletter\n            <tui-icon tuiTooltip="We will not send you spam, pinky promise!" />\n        </label>\n    }\n    <footer>\n        <button\n            appearance="secondary"\n            tuiButton\n            type="button"\n        >\n            Cancel\n        </button>\n        <button\n            tuiButton\n            type="submit"\n        >\n            Submit\n        </button>\n    </footer>\n</form>\n'}}]);