(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[54444],{54444:n=>{n.exports='<p>\n    <label tuiLabel>\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="floating"\n        />\n        Floating visibility\n    </label>\n</p>\n<p>\n    <label tuiLabel>\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="background"\n        />\n        Background visibility\n    </label>\n</p>\n<p>\n    <label tuiLabel>\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="secondAction"\n        />\n        Second action visibility\n    </label>\n</p>\n\n<p>\n    <tui-textfield [iconStart]="\'@tui.paintbrush\'">\n        <label tuiLabel>Background color</label>\n        <input\n            placeholder="#00000000"\n            tuiInputColor\n            [(ngModel)]="color"\n        />\n    </tui-textfield>\n    <label class="label">\n        <span>0%</span>\n        <span>Opacity</span>\n        <span>100%</span>\n    </label>\n</p>\n\n<div class="content">\n    <div\n        *tuiRepeatTimes="let i of 30"\n        tuiCell\n    >\n        <div\n            appearance="primary"\n            tuiAvatar="@tui.star"\n        ></div>\n        <div tuiTitle>\n            Title\n            <div tuiSubtitle>Description</div>\n        </div>\n    </div>\n\n    @if (floating) {\n        <footer\n            @tuiSlideInTop\n            [tuiFloatingContainer]="background ? color : \'transparent\'"\n        >\n            <button\n                tuiButton\n                type="button"\n            >\n                Main action\n            </button>\n            @if (secondAction) {\n                <button\n                    appearance="flat"\n                    tuiButton\n                    type="button"\n                    @tuiHeightCollapse\n                >\n                    Secondary action\n                </button>\n            }\n        </footer>\n    }\n</div>\n'}}]);