(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[56166],{56166:n=>{n.exports='<table\n    tuiTable\n    class="table"\n    [columns]="columns"\n>\n    <thead>\n        <tr tuiThGroup>\n            <th tuiTh></th>\n            <th\n                tuiTh\n                [minWidth]="50"\n            >\n                #\n            </th>\n            <th\n                tuiTh\n                [minWidth]="150"\n            >\n                Name\n            </th>\n            <th\n                tuiTh\n                [minWidth]="150"\n            >\n                Date of Birth\n            </th>\n            <th tuiTh>Age</th>\n        </tr>\n    </thead>\n    <tbody\n        tuiTbody\n        [data]="data"\n    >\n        <ng-container *ngFor="let item of data; index as i">\n            <tr tuiTr>\n                <td\n                    *tuiCell="\'children\'"\n                    tuiTd\n                >\n                    <button\n                        appearance="flat-grayscale"\n                        size="xs"\n                        tuiIconButton\n                        type="button"\n                        class="chevron-btn"\n                        [attr.title]="item.children?.length ? \'\' : \'No children\'"\n                        [disabled]="!item.children?.length"\n                        [style.border-radius]="\'100%\'"\n                        [tuiChevron]="rowState[i] ?? false"\n                        (click)="toggleRow(i)"\n                    >\n                        Toggle\n                    </button>\n                </td>\n                <td\n                    *tuiCell="\'#\'"\n                    tuiTd\n                >\n                    {{ i + 1 }}\n                </td>\n                <td\n                    *tuiCell="\'name\'"\n                    tuiTd\n                >\n                    {{ item.name }}\n                </td>\n                <td\n                    *tuiCell="\'dob\'"\n                    tuiTd\n                >\n                    {{ item.dob }}\n                </td>\n                <td\n                    *tuiCell="\'age\'"\n                    tuiTd\n                >\n                    {{ item.age }}\n                </td>\n            </tr>\n\n            <tui-table-expand\n                *ngIf="item.children?.length"\n                [expanded]="rowState[i] ?? false"\n            >\n                <tr\n                    *ngFor="let child of item.children; index as j"\n                    tuiTr\n                    class="child"\n                >\n                    <td\n                        *tuiCell="\'children\'"\n                        tuiTd\n                    ></td>\n                    <td\n                        *tuiCell="\'#\'"\n                        tuiTd\n                    >\n                        {{ i + 1 }}.{{ j + 1 }}\n                    </td>\n                    <td\n                        *tuiCell="\'name\'"\n                        tuiTd\n                    >\n                        {{ child.name }}\n                    </td>\n                    <td\n                        *tuiCell="\'dob\'"\n                        tuiTd\n                    >\n                        {{ child.dob }}\n                    </td>\n                    <td\n                        *tuiCell="\'age\'"\n                        tuiTd\n                    >\n                        {{ child.age }}\n                    </td>\n                </tr>\n            </tui-table-expand>\n        </ng-container>\n    </tbody>\n</table>\n'}}]);