(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[65453],{65453:n=>{n.exports='<tui-accordion class="accordion">\n    @for (group of operations | keyvalue: orderBy; track group) {\n        <button\n            appearance=""\n            iconEnd=""\n            tuiAccordion\n            tuiCell\n        >\n            <span tuiTitle>\n                <strong>{{ group.key }}</strong>\n                <span tuiSubtitle>\n                    @if (group.value.length) {\n                        {{ group.value.length }} operations â€¢ Total:\n                        {{ sum(group.value) | tuiAmount: \'$\' : \'left\' | async }}\n                    } @else {\n                        No operations so far\n                    }\n                </span>\n            </span>\n        </button>\n        <tui-expand [style.display]="group.value.length ? null : \'none\'">\n            @for (operation of group.value; track operation) {\n                <div tuiCell>\n                    <tui-avatar\n                        [appearance]="operation.sum && operation.sum > 0 ? \'\' : \'negative\'"\n                        [src]="getIcon(operation)"\n                    />\n                    <span tuiTitle>\n                        <strong>{{ operation.title }}</strong>\n                        @if (operation.subtitle) {\n                            <span tuiSubtitle>\n                                {{ operation.subtitle }}\n                            </span>\n                        }\n                    </span>\n                    @if (operation.sum) {\n                        <span\n                            tuiTitle\n                            [style.color]="operation.sum > 0 ? \'var(--tui-text-positive)\' : null"\n                        >\n                            {{ operation.sum | tuiAmount: \'$\' : \'left\' | async }}\n                            <span tuiSubtitle>{{ operation.time }}</span>\n                        </span>\n                    } @else {\n                        <button\n                            appearance="secondary"\n                            tuiButton\n                            type="button"\n                        >\n                            Retry\n                        </button>\n                    }\n                </div>\n            }\n        </tui-expand>\n    }\n</tui-accordion>\n'}}]);