(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[70599],{70599:t=>{t.exports='<tui-notification>\n    When mobile styles are enabled you can use\n    <code style="white-space: nowrap">input[type=\'search\'][tuiSearch]</code>\n    to imitate iOS native input\n</tui-notification>\n<p>\n    <button\n        tuiButton\n        type="button"\n        (click)="open.set(true)"\n    >\n        Show\n    </button>\n</p>\n<ng-template #bar>\n    <tui-app-bar>\n        <button\n            tuiButton\n            tuiSlot="left"\n            type="button"\n            (click)="open.set(false)"\n        >\n            Close\n        </button>\n        Search contacts\n    </tui-app-bar>\n    <input\n        placeholder="Search contacts"\n        tuiSearch\n        type="search"\n        class="input"\n        [(ngModel)]="search"\n    />\n</ng-template>\n<ng-template\n    [tuiSheetDialog]="open()"\n    [tuiSheetDialogOptions]="{label: bar, bar: false, fullscreen: true}"\n    (tuiSheetDialogChange)="open.set($event)"\n>\n    <div class="favorites">\n        @for (item of items | slice: 0 : 6; track item) {\n            <tui-avatar-labeled [label]="item.name">\n                <div [tuiAvatar]="item.avatar | tuiFallbackSrc: \'@tui.user\' | async"></div>\n            </tui-avatar-labeled>\n        }\n    </div>\n    <div class="items">\n        @for (item of items | tuiFilter: filter : search; track item) {\n            <button\n                tuiCell\n                type="button"\n                class="cell"\n                (click)="open.set(false)"\n            >\n                <div [tuiAvatar]="item.avatar | tuiFallbackSrc: \'@tui.user\' | async"></div>\n                <span tuiTitle>\n                    <span tuiFade>{{ item.name }}</span>\n                    <span tuiSubtitle>{{ item.email }}</span>\n                </span>\n            </button>\n        }\n    </div>\n    <footer tuiFloatingContainer>\n        <button\n            tuiButton\n            type="button"\n            class="button"\n            (click)="open.set(false)"\n        >\n            Add contact\n        </button>\n    </footer>\n</ng-template>\n'}}]);