(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[71111],{71111:n=>{n.exports='<tui-segmented>\n    @for (state of statuses; track state) {\n        <button\n            type="button"\n            (click)="status = state"\n        >\n            {{ state }}\n        </button>\n    }\n</tui-segmented>\n\n<tui-textfield>\n    <label tuiLabel>Card number</label>\n    <input\n        icon=""\n        tuiInputCard\n        [disabled]="status === \'disabled\'"\n        [invalid]="status === \'invalid\'"\n        [readOnly]="status === \'readOnly\'"\n        [(ngModel)]="card"\n    />\n    @if (card.startsWith(\'1234\')) {\n        <tui-thumbnail-card\n            iconStart="@tui.dollar-sign"\n            paymentSystem="visa"\n            size="s"\n            [style.background]="background"\n        />\n    }\n</tui-textfield>\n'}}]);