(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[75117],{75117:n=>{n.exports='<button\n    tuiButton\n    type="button"\n    (click)="onClick()"\n>\n    Show dialog\n</button>\n<ng-template let-observer>\n    <tui-app-bar tuiAppBarSize>\n        @if (step > 1) {\n            <button\n                tuiButton\n                tuiSlot="left"\n                type="button"\n                (click)="onStep(-1)"\n            >\n                Back\n            </button>\n        }\n        <progress\n            size="s"\n            tuiProgressBar\n            [max]="3"\n            [style.width.rem]="10"\n            [value]="step"\n        ></progress>\n        <button\n            tuiButton\n            tuiSlot="right"\n            type="button"\n            (click)="observer.complete()"\n        >\n            Close\n        </button>\n    </tui-app-bar>\n    <section [tuiSlides]="direction">\n        @switch (step) {\n            @case (1) {\n                <div>\n                    <header>Welcome to the slides demo</header>\n                    <p>\n                        These wrapping components will be animated upon navigating this modal dialog. This works well on\n                        both desktop and mobile. In your own layouts watch out for unwanted scrollbars.\n                    </p>\n                </div>\n            }\n            @case (2) {\n                <div>\n                    <header>Header is optional</header>\n                    <section [style.margin]="\'0 -1rem\'">\n                        <div\n                            *tuiRepeatTimes="let _ of 5"\n                            tuiCell\n                        >\n                            <tui-avatar\n                                appearance="primary"\n                                src="@tui.star"\n                            />\n                            <div tuiTitle>\n                                Title\n                                <div tuiSubtitle>Description</div>\n                            </div>\n                        </div>\n                    </section>\n                </div>\n            }\n            @case (3) {\n                <div\n                    tuiAppearance="floating"\n                    tuiCardLarge\n                >\n                    <header tuiHeader>\n                        <h1 tuiTitle>\n                            Title\n                            <span tuiSubtitle>Subtitle</span>\n                        </h1>\n                        <aside tuiAccessories>\n                            <tui-avatar\n                                appearance="primary"\n                                src="@tui.star"\n                            />\n                        </aside>\n                    </header>\n                    <footer>\n                        <button\n                            appearance="secondary"\n                            size="m"\n                            tuiButton\n                            type="button"\n                        >\n                            Label\n                        </button>\n                    </footer>\n                </div>\n            }\n        }\n    </section>\n    <footer\n        tuiFloatingContainer\n        [style.margin-block-start]="\'auto\'"\n    >\n        <button\n            tuiButton\n            type="button"\n            (click)="step < 3 ? onStep(1) : observer.complete()"\n        >\n            {{ step < 3 ? \'Next\' : \'OK\' }}\n        </button>\n    </footer>\n</ng-template>\n'}}]);