(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[75380],{75380:n=>{n.exports='<form\n    tuiForm="l"\n    [formGroup]="testForm"\n    [style.max-width.rem]="35"\n>\n    <header tuiHeader>\n        <h3 tuiTitle>\n            A header\n            <span tuiSubtitle>Form with large controls</span>\n        </h3>\n    </header>\n\n    <tui-textfield>\n        <label tuiLabel>Textfield</label>\n        <input\n            formControlName="nameValue"\n            placeholder="Field placeholder"\n            tuiTextfield\n        />\n        <tui-icon tuiTooltip="A tooltip" />\n    </tui-textfield>\n\n    <tui-error\n        formControlName="nameValue"\n        [error]="[] | tuiFieldError | async"\n    />\n\n    <tui-input-date formControlName="periodValue">\n        Input date\n        <input\n            placeholder="Placeholder"\n            tuiTextfieldLegacy\n        />\n    </tui-input-date>\n    <tui-error\n        formControlName="periodValue"\n        [error]="[] | tuiFieldError | async"\n    />\n\n    <fieldset>\n        <div class="stack">\n            <tui-textfield>\n                <label tuiLabel>Input password</label>\n                <input\n                    formControlName="passwordValue"\n                    tuiTextfield\n                    type="password"\n                />\n                <tui-icon tuiPassword />\n            </tui-textfield>\n\n            <tui-error\n                formControlName="passwordValue"\n                [error]="[] | tuiFieldError | async"\n            />\n        </div>\n        <div class="stack">\n            <tui-textfield>\n                <label tuiLabel>Input money</label>\n                <input\n                    formControlName="moneyValue"\n                    tuiInputNumber\n                    [postfix]="\' \' + \'RUB\' | tuiCurrency"\n                />\n            </tui-textfield>\n            <tui-error\n                formControlName="moneyValue"\n                [error]="[] | tuiFieldError | async"\n            />\n        </div>\n    </fieldset>\n\n    <tui-input-slider\n        formControlName="quantityValue"\n        tuiTextfieldPostfix="₽"\n        [max]="3000000"\n        [min]="50000"\n        [segments]="1"\n    >\n        Some slider\n    </tui-input-slider>\n    <div class="ticks-labels">\n        <span>from 50 000 ₽</span>\n        <span>to 3 000 000 ₽</span>\n    </div>\n\n    <fieldset>\n        <div class="stack">\n            <tui-textfield\n                tuiChevron\n                [tuiTextfieldCleaner]="false"\n            >\n                <label tuiLabel>Choose a person</label>\n\n                <input\n                    formControlName="personValue"\n                    tuiSelect\n                />\n\n                <tui-data-list-wrapper\n                    *tuiTextfieldDropdown\n                    new\n                    [items]="persons"\n                />\n            </tui-textfield>\n            <tui-error\n                formControlName="personValue"\n                [error]="[] | tuiFieldError | async"\n            />\n        </div>\n        <div class="stack">\n            <tui-input-phone formControlName="phoneValue">Input phone</tui-input-phone>\n            <tui-error\n                formControlName="phoneValue"\n                [error]="[] | tuiFieldError | async"\n            />\n        </div>\n    </fieldset>\n    <div\n        tuiGroup\n        [collapsed]="true"\n    >\n        <label tuiBlock>\n            Option 1\n            <input\n                formControlName="radioValue"\n                tuiRadio\n                type="radio"\n                value="with-commission"\n                [style.margin-left]="\'auto\'"\n            />\n        </label>\n        <label tuiBlock>\n            Option 2\n            <input\n                formControlName="radioValue"\n                tuiRadio\n                type="radio"\n                value="without-commission"\n                [style.margin-left]="\'auto\'"\n            />\n        </label>\n    </div>\n    <fieldset>\n        <label tuiLabel>\n            <input\n                formControlName="osnoValue"\n                tuiCheckbox\n                type="checkbox"\n            />\n            First option\n        </label>\n        <label tuiLabel>\n            <input\n                formControlName="usnValue"\n                tuiCheckbox\n                type="checkbox"\n            />\n            Easter Egg\n        </label>\n    </fieldset>\n    <fieldset>\n        <label tuiLabel>\n            <input\n                formControlName="eshnValue"\n                tuiCheckbox\n                type="checkbox"\n            />\n            Boring option\n        </label>\n        <label tuiLabel>\n            <input\n                formControlName="envdValue"\n                tuiCheckbox\n                type="checkbox"\n            />\n            Interesting option\n        </label>\n    </fieldset>\n\n    <footer>\n        <button\n            tuiButton\n            type="submit"\n        >\n            Submit\n        </button>\n        <button\n            appearance="flat"\n            tuiButton\n            type="button"\n        >\n            Cancel\n        </button>\n    </footer>\n</form>\n'}}]);