(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[83235],{83235:n=>{n.exports='<tui-tiles\n    class="tiles"\n    [debounce]="500"\n    [(order)]="order"\n>\n    @for (item of items; track item; let i = $index) {\n        <tui-tile\n            class="tile"\n            [style.order]="order.get(i)"\n        >\n            <div class="content">\n                <tui-icon\n                    icon="@tui.grip-vertical"\n                    tuiTileHandle\n                    class="handle"\n                />\n                <strong>{{ item.content }}</strong>\n                <p>Order - {{ order.get(i) ?? i }}</p>\n                <tui-tiles\n                    class="nested"\n                    [(order)]="item.order"\n                >\n                    @for (child of items; track child; let j = $index) {\n                        <tui-tile\n                            tuiTileHandle\n                            class="tile"\n                            [style.order]="item.order.get(j)"\n                        >\n                            <div class="content">\n                                {{ child.content }}\n                            </div>\n                        </tui-tile>\n                    }\n                </tui-tiles>\n            </div>\n        </tui-tile>\n    }\n</tui-tiles>\n'}}]);