(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[86658],{86658:n=>{n.exports='<tui-notification>\n    Don\'t forget import\n    <code>TuiCardLarge</code>\n</tui-notification>\n\n<div\n    tuiAppearance="floating"\n    tuiCardLarge\n>\n    <h3 tuiTitle="m">Inside a block</h3>\n    <button\n        *ngFor="let item of items"\n        tuiCell\n        type="button"\n    >\n        <tui-avatar\n            appearance="accent"\n            [src]="item.icon"\n        />\n        <span tuiTitle>\n            {{ item.title }}\n            <span tuiSubtitle>{{ item.subtitle }}</span>\n        </span>\n    </button>\n</div>\n\n<label>\n    Inside a dropdown\n    <tui-textfield\n        tuiChevron\n        [content]="content"\n        [tuiTextfieldCleaner]="false"\n    >\n        <input\n            tuiSelect\n            [(ngModel)]="value"\n        />\n\n        <tui-data-list-wrapper\n            *tuiTextfieldDropdown\n            new\n            [itemContent]="content"\n            [items]="items"\n        />\n        <ng-template\n            #content\n            let-item\n        >\n            <span tuiCell>\n                <tui-avatar\n                    appearance="primary"\n                    [src]="item.icon"\n                />\n                <span tuiTitle>\n                    {{ item.title }}\n                    <span tuiSubtitle>{{ item.subtitle }}</span>\n                </span>\n            </span>\n        </ng-template>\n    </tui-textfield>\n</label>\n'}}]);