(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[14860],{14860:e=>{e.exports='<tui-textfield>\n    <label tuiLabel>{{ user() || \'Phone number or name\' }}</label>\n\n    <input\n        #input\n        mask="+1 (###)###-####"\n        tuiInputPhone\n        [allowText]="true"\n        [ngModel]="value()"\n        (input)="onInput(input.value)"\n        (ngModelChange)="value.set($event)"\n    />\n\n    @if (items | tuiFilterByInput: matcher; as filtered) {\n        @if (!user() && input.value && filtered.length) {\n            <tui-data-list-wrapper\n                *tuiDropdown\n                new\n                [itemContent]="template"\n                [items]="filtered"\n                (itemClick)="selectUser($event)"\n            />\n        }\n    }\n</tui-textfield>\n\n<ng-template\n    #template\n    let-user\n>\n    <div tuiCell>\n        <div tuiAvatar="@tui.user">\n            <img\n                alt=""\n                [src]="user.avatarUrl"\n            />\n        </div>\n        <div tuiTitle>\n            {{ user }}\n            <span tuiSubtitle>{{ user.phone }}</span>\n        </div>\n    </div>\n</ng-template>\n\n<pre>value: {{ value() }}</pre>\n'}}]);