(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[17974],{17974:n=>{n.exports='<button\n    tuiButton\n    type="button"\n    (click)="step.set(step() || 1)"\n>\n    Show popover\n</button>\n<div\n    *tuiPopup="!!step()"\n    tuiAppearance="floating"\n    tuiCardLarge="compact"\n    class="popover"\n    [@tuiFadeIn]="animation"\n    [@tuiSlideIn]="animation"\n>\n    <ng-container *ngIf="step() === 1">\n        <h1 tuiTitle>\n            Leave your feedback\n            <span tuiSubtitle>It will only take 3 minutes</span>\n        </h1>\n        <tui-rating [(ngModel)]="rating" />\n    </ng-container>\n    <ng-container *ngIf="step() === 2">\n        <h1 tuiTitle>Why so?</h1>\n        <tui-textarea\n            tuiTextfieldSize="m"\n            [tuiTextfieldLabelOutside]="true"\n            [(ngModel)]="comment"\n        >\n            Leave a few words\n            <textarea\n                tuiAutoFocus\n                tuiTextfieldLegacy\n            ></textarea>\n        </tui-textarea>\n    </ng-container>\n    <button\n        appearance="icon"\n        iconStart="@tui.x"\n        size="xs"\n        tuiIconButton\n        type="button"\n        class="close"\n        (click)="close()"\n    >\n        Close\n    </button>\n    <footer class="footer">\n        <button\n            *ngIf="step() === 2"\n            appearance="secondary"\n            size="s"\n            tuiButton\n            type="button"\n            (click)="step.set(1)"\n        >\n            Back\n        </button>\n        <button\n            appearance="primary"\n            size="s"\n            tuiButton\n            type="button"\n            (click)="step() === 1 ? step.set(2) : close()"\n        >\n            {{ step() === 1 ? \'Next\' : \'Submit\' }}\n        </button>\n    </footer>\n</div>\n'}}]);