(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[2327],{2327:n=>{n.exports='<form\n    tuiForm="m"\n    [formGroup]="form"\n>\n    <header tuiHeader>\n        <hgroup tuiTitle>\n            <h3>Contact list</h3>\n            <tui-error\n                formArrayName="phones"\n                [style.margin-block-start.rem]="-0.25"\n            />\n        </hgroup>\n    </header>\n    <ng-container\n        formArrayName="phones"\n        [tuiTextfieldCleaner]="false"\n    >\n        @for (phone of form.controls.phones.controls; track $index) {\n            <label tuiLabel>\n                Phone number {{ $index + 1 }}\n                <tui-textfield>\n                    <input\n                        tuiInputPhone\n                        [formControlName]="$index"\n                    />\n                    <button\n                        appearance="icon"\n                        iconStart="@tui.trash"\n                        size="xs"\n                        tuiIconButton\n                        type="button"\n                        (click)="removePhone($index)"\n                    >\n                        Remove\n                    </button>\n                </tui-textfield>\n            </label>\n            <tui-error [formControlName]="$index" />\n        }\n    </ng-container>\n    <footer>\n        <button\n            size="s"\n            tuiButton\n            type="button"\n            (click)="addPhone()"\n        >\n            Add a phone number\n        </button>\n    </footer>\n</form>\n'}}]);