(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[23429],{23429:n=>{n.exports='<label tuiLabel>\n    <input\n        tuiSwitch\n        type="checkbox"\n        [(ngModel)]="loading"\n    />\n    Loading\n</label>\n\n<span [style.font]="\'var(--tui-font-text-xl)\'">Shimmer</span>\n\n<section\n    tuiAppearance="floating"\n    tuiCardLarge\n>\n    <header tuiHeader>\n        <hgroup\n            tuiTitle\n            [tuiShimmer]="loading"\n        >\n            <h5>You got $237&nbsp;000,42 left</h5>\n            <p tuiSubtitle>Where\'s the money, Lebowski?</p>\n        </hgroup>\n\n        <aside tuiAccessories>\n            <tui-avatar\n                appearance="primary"\n                src="@tui.star"\n            />\n        </aside>\n    </header>\n    <tui-avatar-stack [tuiShimmer]="loading">\n        <tui-avatar\n            *ngFor="let avatar of avatars"\n            [src]="avatar"\n        />\n    </tui-avatar-stack>\n    <footer>\n        <button\n            appearance="secondary"\n            size="m"\n            tuiButton\n            type="button"\n        >\n            It\'s down there\n        </button>\n        <button\n            size="m"\n            tuiButton\n            type="button"\n        >\n            Take another look\n        </button>\n    </footer>\n</section>\n\n<span [style.font]="\'var(--tui-font-text-xl)\'">Skeleton</span>\n\n<section\n    tuiAppearance="floating"\n    tuiCardLarge\n>\n    <header tuiHeader>\n        <hgroup tuiTitle>\n            <h5 [tuiSkeleton]="loading && 5">\n                {{ loading ? \'\' : \'You got $237&nbsp;000,42 left\' }}\n            </h5>\n            <p\n                tuiSubtitle\n                [tuiSkeleton]="loading && 6"\n            >\n                {{ loading ? \'\' : "Where\'s the money, Lebowski?" }}\n            </p>\n        </hgroup>\n\n        <aside tuiAccessories>\n            <tui-avatar\n                appearance="primary"\n                src="@tui.star"\n                [tuiSkeleton]="loading"\n            />\n        </aside>\n    </header>\n    <tui-avatar-stack>\n        <tui-avatar\n            *ngFor="let avatar of avatars"\n            [src]="avatar"\n            [tuiSkeleton]="loading"\n        />\n    </tui-avatar-stack>\n    <footer>\n        <button\n            appearance="secondary"\n            size="m"\n            tuiButton\n            type="button"\n        >\n            It\'s down there\n        </button>\n        <button\n            size="m"\n            tuiButton\n            type="button"\n        >\n            Take another look\n        </button>\n    </footer>\n</section>\n'}}]);