(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[31961],{31961:n=>{n.exports='<p>\n    <label tuiLabel>\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="floating"\n        />\n        Floating visibility\n    </label>\n</p>\n<p>\n    <label tuiLabel>\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="secondAction"\n        />\n        Second action visibility\n    </label>\n</p>\n<p>\n    <label tuiLabel>\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="action"\n        />\n        Primary action\n    </label>\n</p>\n\n<div class="content">\n    <div\n        *tuiRepeatTimes="let i of 30"\n        tuiCell\n    >\n        <tui-avatar\n            appearance="primary"\n            src="@tui.star"\n        />\n        <div tuiTitle>\n            Title\n            <div tuiSubtitle>Description</div>\n        </div>\n    </div>\n\n    @if (floating) {\n        <footer\n            @tuiSlideInTop\n            [tuiFloatingContainer]="secondAction ? \'\' : \'transparent\'"\n        >\n            @if (!action) {\n                <button\n                    tuiButton\n                    type="button"\n                    [@tuiCrossFade]="{duration: 0.25} | tuiAnimation"\n                >\n                    Main action\n                </button>\n            }\n            @if (action) {\n                <button\n                    appearance="primary-grayscale"\n                    tuiButton\n                    type="button"\n                    [@tuiCrossFade]="{duration: 0.25} | tuiAnimation"\n                >\n                    Add to Apple Wallet\n                </button>\n            }\n            @if (secondAction) {\n                <button\n                    appearance="flat"\n                    tuiButton\n                    type="button"\n                    @tuiHeightCollapse\n                >\n                    Secondary action\n                </button>\n            }\n        </footer>\n    }\n</div>\n'}}]);