(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[33263],{33263:n=>{n.exports='<tui-notification>\n    <div>\n        Can be attached as host directive:\n        <code>hostDirectives: [TuiConnected]</code>\n    </div>\n</tui-notification>\n\n<div\n    tuiCardLarge\n    tuiConnected\n    tuiSurface="elevated"\n>\n    <h3 tuiTitle="m">Inside a block</h3>\n    <button\n        *ngFor="let item of items"\n        tuiCell\n        type="button"\n    >\n        <tui-avatar\n            appearance="accent"\n            [src]="item.icon"\n        />\n        <span tuiTitle>\n            {{ item.title }}\n            <span tuiSubtitle>{{ item.subtitle }}</span>\n        </span>\n    </button>\n</div>\n\n<div tuiConnected>\n    <label tuiCell>\n        <tui-badged-content>\n            <tui-icon\n                appearance="error"\n                icon="@tui.arrow-down-left"\n                size="s"\n                tuiBadge\n                tuiSlot="top"\n            />\n            <tui-avatar src="@tui.phone" />\n        </tui-badged-content>\n        <div\n            tuiTitle\n            [style.color]="\'var(--tui-text-negative)\'"\n        >\n            Allow incoming\n            <div tuiSubtitle>Why would you?</div>\n        </div>\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="incoming"\n        />\n    </label>\n    <label tuiCell>\n        <tui-badged-content tuiAccessories>\n            <tui-icon\n                appearance="success"\n                icon="@tui.arrow-up-right"\n                size="s"\n                tuiBadge\n                tuiSlot="top"\n            />\n            <tui-avatar src="@tui.phone" />\n        </tui-badged-content>\n        <div tuiTitle>Allow outgoing unusual call that can change your life in an unusual way</div>\n        <div tuiAccessories>\n            <input\n                tuiSwitch\n                type="checkbox"\n                [(ngModel)]="outgoing"\n            />\n        </div>\n    </label>\n    <label tuiCell>\n        <tui-badged-content>\n            <tui-icon\n                appearance="error"\n                icon="@tui.arrow-down-left"\n                size="s"\n                tuiBadge\n                tuiSlot="top"\n            />\n            <tui-avatar src="@tui.phone" />\n        </tui-badged-content>\n        <div\n            tuiTitle\n            [style.color]="\'var(--tui-text-negative)\'"\n        >\n            Allow incoming\n            <div tuiSubtitle>Why would you?</div>\n        </div>\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="incoming"\n        />\n    </label>\n</div>\n'}}]);