(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[38735],{38735:n=>{n.exports='<tui-textfield tuiChevron>\n    <input\n        placeholder="Select film"\n        tuiComboBox\n        [(ngModel)]="value"\n    />\n\n    <tui-data-list\n        *tuiTextfieldDropdown\n        size="s"\n    >\n        <section class="filters">\n            <label\n                *ngFor="let category of categories"\n                tuiChip\n                [appearance]="filters[category] ? \'accent\' : \'neutral\'"\n                (pointerdown.prevent)="(0)"\n            >\n                {{ category }}\n                <input\n                    hidden\n                    type="checkbox"\n                    [(ngModel)]="filters[category]"\n                />\n            </label>\n        </section>\n\n        <ng-container *ngFor="let group of filmDatabase | keyvalue">\n            <tui-opt-group\n                *ngIf="filters[group.key]"\n                [label]="group.key"\n            >\n                <button\n                    *ngFor="let film of group.value | tuiFilterByInput"\n                    new\n                    tuiOption\n                    type="button"\n                    [value]="film"\n                >\n                    {{ film }}\n                </button>\n            </tui-opt-group>\n        </ng-container>\n    </tui-data-list>\n</tui-textfield>\n'}}]);