(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[41007],{41007:n=>{n.exports='<tui-stepper\n    [activeItemIndex]="index"\n    (activeItemIndexChange)="onStep($event)"\n>\n    <button tuiStep>Personal details</button>\n    <button tuiStep>Shipping address</button>\n    <button tuiStep>Payment info</button>\n</tui-stepper>\n<section [tuiSlides]="direction">\n    @for (form of forms; track form; let i = $index) {\n        @if (i === index) {\n            <form\n                tuiAppearance="floating"\n                tuiCardLarge\n                tuiForm\n                [formGroup]="form"\n                [id]="`form_${i}`"\n                (ngSubmit)="onSubmit()"\n            >\n                <header tuiHeader>\n                    <h2 tuiTitle>\n                        Registration form\n                        <span tuiSubtitle>Tell us about yourself</span>\n                    </h2>\n                </header>\n                @for (control of form.controls | keyvalue; track control; let j = $index) {\n                    <tui-textfield>\n                        <label tuiLabel>{{ control.key }}</label>\n                        <input\n                            tuiTextfield\n                            [formControlName]="control.key"\n                            [tuiAutoFocus]="!!index && !j"\n                        />\n                    </tui-textfield>\n                }\n            </form>\n        }\n    }\n</section>\n<footer>\n    <button\n        appearance="secondary"\n        tuiButton\n        type="button"\n        [disabled]="!index"\n        (click)="index = index - 1"\n    >\n        Back\n    </button>\n\n    <button\n        tuiButton\n        type="submit"\n        [attr.form]="`form_${index}`"\n    >\n        Next\n    </button>\n</footer>\n'}}]);