(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[50862],{50862:n=>{n.exports='<tui-notification>\n    <code>TuiLineDaysChart</code>\n    is used to show data of several months to simplify working with different number of days in months\n</tui-notification>\n<p class="controls">\n    <tui-textfield [tuiTextfieldCleaner]="false">\n        <label tuiLabel>Data</label>\n        <input\n            tuiInputDateRange\n            [maxLength]="maxLength"\n            [(ngModel)]="data"\n            (ngModelChange)="onDataChange($event)"\n        />\n        <tui-calendar-range *tuiDropdown />\n    </tui-textfield>\n    <tui-textfield\n        class="tui-space_left-4"\n        [tuiTextfieldCleaner]="false"\n    >\n        <label tuiLabel>Visible range</label>\n        <input\n            tuiInputDateRange\n            [maxLength]="maxLength"\n            [(ngModel)]="show"\n        />\n        <tui-calendar-range *tuiDropdown />\n    </tui-textfield>\n</p>\n<p class="legend">\n    @for (chart of days; track chart) {\n        <span class="item">\n            <small class="name">Chart {{ $index + 1 }}</small>\n        </span>\n    }\n</p>\n@if (labels(show) | async; as labels) {\n    <tui-axes\n        class="axes"\n        [axisXLabels]="labels"\n        [horizontalLines]="4"\n        [tuiLineChartHint]="hint"\n        [verticalLines]="labels.length - 1"\n    >\n        @if (getWidth(show) > 90) {\n            @for (chart of days; track chart) {\n                <tui-line-days-chart\n                    class="chart"\n                    [height]="200"\n                    [value]="chart | tuiFilter: filter : range"\n                />\n            }\n        } @else {\n            @for (chart of days; track chart) {\n                <tui-line-chart\n                    class="chart"\n                    [height]="200"\n                    [value]="chart | tuiFilter: filter : range | tuiMapper: toNumbers : range"\n                    [width]="getWidth(range)"\n                />\n            }\n        }\n    </tui-axes>\n}\n\n<ng-template\n    #hint\n    let-data\n>\n    <div class="tui-space_bottom-2">\n        <strong>{{ getDate(data[0][0], range.from) }}</strong>\n    </div>\n    <div>\n        @for (point of data; track point) {\n            <div class="item">\n                <span class="value">${{ point[1].toFixed(0) }}</span>\n            </div>\n        }\n    </div>\n</ng-template>\n'}}]);