(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[50901],{50901:n=>{n.exports='<button\n    size="m"\n    tuiButton\n    type="button"\n    class="tui-space_bottom-4"\n    (click)="show()"\n>\n    Show preview\n</button>\n\n<ng-template\n    #preview\n    let-preview\n>\n    <tui-preview\n        [rotatable]="!(contentUnavailable$ | async)"\n        [zoomable]="!(contentUnavailable$ | async) && !(loading$ | async)"\n    >\n        <tui-preview-title>{{ title$ | async }}</tui-preview-title>\n        <tui-preview-pagination\n            [index]="index$$.value"\n            [length]="items.length"\n            (indexChange)="index$$.next($event)"\n        />\n\n        <button\n            iconStart="@tui.download"\n            tuiIconButton\n            tuiPreviewAction\n            type="button"\n            (click)="download()"\n        >\n            Download\n        </button>\n\n        <button\n            iconStart="@tui.x"\n            tuiIconButton\n            tuiPreviewAction\n            type="button"\n            (click)="preview.complete()"\n        >\n            Close\n        </button>\n\n        @if (contentUnavailable$ | async) {\n            <div\n                tuiTheme="dark"\n                class="t-container"\n            >\n                <tui-icon\n                    icon="@tui.file"\n                    class="t-icon"\n                />\n                <div>Preview unavailable</div>\n            </div>\n        }\n\n        @if (imageSrc$ | async; as src) {\n            <img\n                alt="img source"\n                height="512"\n                width="512"\n                [src]="src"\n            />\n        }\n\n        @if (loading$ | async) {\n            <tui-loader\n                size="xl"\n                class="t-loader"\n            />\n        }\n    </tui-preview>\n</ng-template>\n'}}]);