(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[51012],{51012:n=>{n.exports='<tui-scrollbar\n    waIntersectionRoot\n    class="scrollbar"\n    [hidden]="true"\n>\n    <table\n        size="l"\n        tuiTable\n        class="table"\n        [columns]="columns"\n        [direction]="-1"\n        [sorter]="totalSorter"\n    >\n        <thead tuiThead>\n            <tr tuiThGroup>\n                <th\n                    *tuiHead="\'name\'"\n                    rowspan="2"\n                    tuiTh\n                    class="first"\n                    [sorter]="null"\n                    [sticky]="true"\n                >\n                    Name\n                </th>\n                <th\n                    *tuiHead="\'price\'"\n                    rowspan="2"\n                    tuiTh\n                    class="number second"\n                    [sticky]="true"\n                >\n                    Price,&nbsp;$\n                </th>\n                <th\n                    *tuiHead="\'quantity\'"\n                    colspan="2"\n                    tuiTh\n                    [sorter]="null"\n                >\n                    Purchase\n                </th>\n                <ng-container *tuiHead="\'unit\'" />\n                <th\n                    *tuiHead="\'date\'"\n                    rowspan="2"\n                    tuiTh\n                    [minWidth]="155"\n                >\n                    Date\n                </th>\n                <th\n                    *tuiHead="\'total\'"\n                    rowspan="2"\n                    tuiTh\n                    class="number"\n                    [sorter]="totalSorter"\n                >\n                    Total\n                </th>\n            </tr>\n            <tr tuiThGroup>\n                <th\n                    *tuiHead="\'quantity\'"\n                    tuiTh\n                    class="number border"\n                >\n                    Quantity\n                </th>\n                <th\n                    *tuiHead="\'unit\'"\n                    tuiTh\n                >\n                    Units\n                </th>\n            </tr>\n        </thead>\n        @let sortedPythons = pythons | tuiTableSort;\n        <tbody\n            heading="Monty Python"\n            tuiTbody\n            [data]="sortedPythons"\n        >\n            @for (item of sortedPythons; track trackByIndex($index)) {\n                <tr tuiTr>\n                    <th\n                        *tuiCell="\'name\'"\n                        tuiTd\n                        [colSpan]="item.price > 1000 ? 2 : 0"\n                    >\n                        <tui-textfield>\n                            <textarea\n                                tuiTextarea\n                                [ngModel]="item.name"\n                                [ngModelOptions]="options"\n                                (ngModelChange)="onValueChange($event, \'name\', item, pythons)"\n                            ></textarea>\n                        </tui-textfield>\n                    </th>\n                    <ng-container *tuiCell="\'price\'">\n                        @if (item.price <= 1000) {\n                            <th\n                                tuiTd\n                                class="second"\n                            >\n                                <tui-textfield>\n                                    <input\n                                        tuiInputNumber\n                                        class="number"\n                                        [ngModel]="item.price"\n                                        [ngModelOptions]="options"\n                                        [tuiValidator]="minPrice"\n                                        (ngModelChange)="onValueChange($event, \'price\', item, pythons)"\n                                    />\n                                </tui-textfield>\n                            </th>\n                        }\n                    </ng-container>\n                    <td\n                        *tuiCell="\'quantity\'"\n                        tuiTd\n                    >\n                        <tui-textfield>\n                            <input\n                                tuiInputNumber\n                                [ngModel]="item.quantity"\n                                [ngModelOptions]="options"\n                                [tuiNumberFormat]="{precision: 0}"\n                                (ngModelChange)="onValueChange($event, \'quantity\', item, pythons)"\n                            />\n                        </tui-textfield>\n                    </td>\n                    <td\n                        *tuiCell="\'unit\'"\n                        tuiTd\n                    >\n                        <tui-textfield\n                            tuiChevron\n                            class="select"\n                        >\n                            <input\n                                placeholder="Units"\n                                tuiSelect\n                                [ngModel]="item.unit"\n                                [ngModelOptions]="options"\n                                (ngModelChange)="onValueChange($event, \'unit\', item, pythons)"\n                            />\n                            <tui-data-list-wrapper\n                                *tuiDropdown\n                                new\n                                [items]="units"\n                            />\n                        </tui-textfield>\n                    </td>\n                    <td\n                        *tuiCell="\'date\'"\n                        tuiTd\n                    >\n                        <tui-textfield>\n                            <input\n                                tuiInputDate\n                                [ngModel]="item.date"\n                                [ngModelOptions]="options"\n                                (ngModelChange)="onValueChange($event, \'date\', item, pythons)"\n                            />\n                            <tui-calendar *tuiDropdown />\n                        </tui-textfield>\n                    </td>\n                    <td\n                        *tuiCell="\'total\'"\n                        tuiTd\n                        class="number text"\n                    >\n                        {{ getTotal(item) | tuiFormatNumber | async }}\n                    </td>\n                </tr>\n            }\n        </tbody>\n        @let sortedStarwars = starwars | tuiTableSort;\n        <tbody\n            tuiTbody\n            [data]="sortedStarwars"\n            [heading]="template"\n        >\n            @for (item of sortedStarwars; track trackByIndex($index)) {\n                <tr tuiTr>\n                    <th\n                        *tuiCell="\'name\'"\n                        tuiTd\n                    >\n                        <tui-textfield>\n                            <textarea\n                                tuiTextarea\n                                [ngModel]="item.name"\n                                [ngModelOptions]="options"\n                                (ngModelChange)="onValueChange($event, \'name\', item, starwars)"\n                            ></textarea>\n                        </tui-textfield>\n                    </th>\n                    <th\n                        *tuiCell="\'price\'"\n                        tuiTd\n                        class="second"\n                    >\n                        <tui-textfield>\n                            <input\n                                tuiInputNumber\n                                [ngModel]="item.price"\n                                [ngModelOptions]="options"\n                                [tuiValidator]="minPrice"\n                                (ngModelChange)="onValueChange($event, \'price\', item, starwars)"\n                            />\n                        </tui-textfield>\n                    </th>\n                    <td\n                        *tuiCell="\'quantity\'"\n                        tuiTd\n                    >\n                        <tui-textfield>\n                            <input\n                                tuiInputNumber\n                                [ngModel]="item.quantity"\n                                [ngModelOptions]="options"\n                                [tuiNumberFormat]="{precision: 0}"\n                                (ngModelChange)="onValueChange($event, \'quantity\', item, starwars)"\n                            />\n                        </tui-textfield>\n                    </td>\n                    <td\n                        *tuiCell="\'unit\'"\n                        tuiTd\n                    >\n                        <tui-textfield\n                            tuiChevron\n                            class="select"\n                        >\n                            <input\n                                placeholder="Units"\n                                tuiSelect\n                                [ngModel]="item.unit"\n                                [ngModelOptions]="options"\n                                (ngModelChange)="onValueChange($event, \'unit\', item, starwars)"\n                            />\n                            <tui-data-list-wrapper\n                                *tuiDropdown\n                                new\n                                [items]="units"\n                            />\n                        </tui-textfield>\n                    </td>\n                    <td\n                        *tuiCell="\'date\'"\n                        tuiTd\n                    >\n                        <tui-textfield>\n                            <input\n                                tuiInputDate\n                                [ngModel]="item.date"\n                                [ngModelOptions]="options"\n                                (ngModelChange)="onValueChange($event, \'date\', item, starwars)"\n                            />\n                            <tui-calendar *tuiDropdown />\n                        </tui-textfield>\n                    </td>\n                    <td\n                        *tuiCell="\'total\'"\n                        tuiTd\n                        class="number text"\n                    >\n                        {{ getTotal(item) | tuiFormatNumber | async }}\n                    </td>\n                </tr>\n            }\n        </tbody>\n    </table>\n</tui-scrollbar>\n<ng-template #template>\n    <tui-icon\n        icon="@tui.star"\n        class="tui-space_right-3"\n    />\n    Star Wars\n</ng-template>\n'}}]);