(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[52581],{52581:n=>{n.exports='<tui-carousel\n    [draggable]="true"\n    [itemsCount]="1"\n    [(index)]="index"\n    (shift)="opacity = $event"\n>\n    @for (item of items; track item; let i = $index) {\n        <div\n            *tuiItem\n            tuiCardMedium\n            class="card"\n            [style.background]="item.gradient"\n        >\n            <h2 tuiTitle>\n                BANK\n                <span tuiSubtitle>{{ item.title }}</span>\n            </h2>\n            <tui-badge class="money">\n                <tui-icon icon="@tui.wallet" />\n                {{ item.content }}\n            </tui-badge>\n        </div>\n    }\n</tui-carousel>\n\n<tui-pagination\n    size="s"\n    [length]="items.length"\n    [(index)]="index"\n/>\n\n<div\n    tuiButtonGroup\n    [style.background-color]="items[index]?.color"\n>\n    <tui-elastic-container [style.opacity]="opacity * 2">\n        @if (index === 0) {\n            <button type="button">\n                <tui-icon icon="@tui.circle-plus" />\n                Create a payment\n            </button>\n            <button type="button">\n                <tui-icon icon="@tui.circle-plus" />\n                Pay the bill\n            </button>\n            <button type="button">\n                <tui-icon icon="@tui.circle-plus" />\n                Remove from favorites\n            </button>\n        }\n\n        @if (index === 1) {\n            <button type="button">\n                <tui-icon icon="@tui.circle-plus" />\n                Remove from favorites\n            </button>\n        }\n\n        @if (index === 2) {\n            <button type="button">\n                <tui-icon icon="@tui.circle-plus" />\n                Create a payment\n            </button>\n            <button type="button">\n                <tui-icon icon="@tui.circle-plus" />\n                Remove from favorites\n            </button>\n        }\n    </tui-elastic-container>\n</div>\n'}}]);