(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[65262],{65262:n=>{n.exports='<tui-filter\n    size="s"\n    [formControl]="control"\n    [items]="items"\n/>\n\n<tui-action-bar\n    *tuiActionBar="open"\n    [expanded]="!!isMobile() && expanded"\n>\n    <tui-data-list role="menu">\n        <tui-opt-group>\n            <button\n                role="menuitem"\n                tuiOption\n                type="button"\n            >\n                <span>\n                    <tui-icon\n                        icon="@tui.send"\n                        class="tui-space_right-3"\n                    />\n                    Send\n                </span>\n            </button>\n            <button\n                role="menuitem"\n                tuiOption\n                type="button"\n            >\n                <span>\n                    <tui-icon\n                        icon="@tui.trash"\n                        class="tui-space_right-3"\n                    />\n                    Delete\n                </span>\n            </button>\n        </tui-opt-group>\n        <tui-opt-group>\n            <button\n                role="menuitem"\n                tuiOption\n                type="button"\n                [disabled]="selected === items.length"\n                (click)="control.setValue(items)"\n            >\n                <span>\n                    <tui-icon\n                        icon="@tui.layout-grid"\n                        class="tui-space_right-3"\n                    />\n                    Select all\n                </span>\n            </button>\n            <button\n                role="menuitem"\n                tuiOption\n                type="button"\n                (click)="close()"\n            >\n                <span>\n                    <tui-icon\n                        icon="@tui.x"\n                        class="tui-space_right-3"\n                    />\n                    Select none and close\n                </span>\n            </button>\n        </tui-opt-group>\n        <tui-opt-group>\n            <button\n                *tuiRepeatTimes="let index of 5"\n                role="menuitem"\n                tuiOption\n                type="button"\n            >\n                <span>\n                    <tui-icon\n                        icon="@tui.star"\n                        class="tui-space_right-3"\n                    />\n                    Action {{ index + 1 }}\n                </span>\n            </button>\n        </tui-opt-group>\n    </tui-data-list>\n\n    <div>\n        <strong>Selected: {{ selected }} of {{ items.length }}</strong>\n        @if (!isMobile()) {\n            <button\n                tuiLink\n                type="button"\n                class="tui-space_left-3"\n                (click)="toggleSelect()"\n            >\n                {{ selected < items.length ? \'Select all\' : \'Select none\' }}\n            </button>\n        }\n    </div>\n\n    <tui-items-with-more>\n        <ng-container *tuiRepeatTimes="let index of 5">\n            <button\n                *tuiItem\n                iconStart="@tui.star"\n                tuiButton\n                type="button"\n            >\n                Action {{ index + 1 }}\n            </button>\n        </ng-container>\n        <ng-template\n            let-lastIndex\n            tuiMore\n        >\n            <button\n                iconStart="@tui.ellipsis"\n                tuiButton\n                tuiDropdownAlign="right"\n                tuiDropdownOpen\n                type="button"\n                [tuiDropdown]="dropdown"\n            >\n                More\n            </button>\n            <ng-template #dropdown>\n                <tui-data-list size="l">\n                    <ng-container *tuiRepeatTimes="let index of 5">\n                        @if (index > lastIndex) {\n                            <button\n                                tuiOption\n                                type="button"\n                            >\n                                Action {{ index + 1 }}\n                            </button>\n                        }\n                    </ng-container>\n                </tui-data-list>\n            </ng-template>\n        </ng-template>\n    </tui-items-with-more>\n\n    @if (!isMobile()) {\n        <button\n            iconStart="@tui.send"\n            tuiButton\n            type="button"\n        >\n            Send\n        </button>\n    }\n    @if (!isMobile()) {\n        <button\n            iconStart="@tui.trash"\n            tuiButton\n            type="button"\n            (click)="close()"\n        >\n            Delete\n        </button>\n    }\n\n    @if (isMobile()) {\n        <button\n            iconStart="@tui.send"\n            tuiIconButton\n            type="button"\n        >\n            Send\n        </button>\n    }\n    @if (isMobile()) {\n        <button\n            iconStart="@tui.ellipsis"\n            tuiIconButton\n            type="button"\n            (click)="expanded = !expanded"\n        >\n            More\n        </button>\n    }\n\n    @if (!isMobile()) {\n        <button\n            appearance="icon"\n            iconStart="@tui.x"\n            tuiIconButton\n            type="button"\n            (click)="close()"\n        >\n            Close\n        </button>\n    }\n</tui-action-bar>\n'}}]);