(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[71805],{71805:n=>{n.exports='<button\n    tuiButton\n    type="button"\n    (click)="open = true"\n>\n    Show\n</button>\n\n<ng-template #template>\n    <div tuiHeader>\n        <div tuiTitle>\n            Title\n            <div tuiSubtitle>Subtitle</div>\n        </div>\n    </div>\n    <label\n        tuiLabel\n        [style.margin-block-start.rem]="1"\n    >\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="floating"\n        />\n        Floating visibility\n    </label>\n    <label\n        tuiLabel\n        [style.margin-block-start.rem]="0.5"\n    >\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="secondAction"\n        />\n        Second action visibility\n    </label>\n    <input\n        placeholder="Some search"\n        tuiSearch\n        type="search"\n        class="input"\n        [(ngModel)]="search"\n    />\n</ng-template>\n\n<ng-template\n    let-observer\n    [tuiSheetDialogOptions]="{label: template, fullscreen: true}"\n    [(tuiSheetDialog)]="open"\n>\n    <div class="content">\n        <section class="items">\n            @for (item of items | tuiFilter: filter : search; track item) {\n                <div tuiCell>\n                    <div\n                        appearance="primary"\n                        tuiAvatar="@tui.star"\n                    ></div>\n                    <div tuiTitle>\n                        {{ item.title }}\n                        <div tuiSubtitle>{{ item.description }}</div>\n                    </div>\n                </div>\n            }\n        </section>\n\n        @if (floating) {\n            <footer\n                tuiFloatingContainer\n                @tuiSlideInTop\n            >\n                <button\n                    tuiButton\n                    type="button"\n                    (click)="observer.complete()"\n                >\n                    Main action\n                </button>\n                @if (secondAction) {\n                    <button\n                        appearance="flat"\n                        tuiButton\n                        type="button"\n                        @tuiHeightCollapse\n                        (click)="observer.complete()"\n                    >\n                        Secondary action\n                    </button>\n                }\n            </footer>\n        }\n    </div>\n</ng-template>\n'}}]);