(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[9141],{9141:n=>{n.exports='<p\n    tuiTextfieldSize="m"\n    class="filters"\n>\n    <tui-textfield class="input">\n        <label tuiLabel>Find on page</label>\n        <input\n            tuiTextfield\n            [(ngModel)]="search"\n        />\n    </tui-textfield>\n\n    <tui-textfield>\n        <label tuiLabel>Minimum age</label>\n        <input\n            tuiInputNumber\n            [formControl]="minAge"\n            [tuiNumberFormat]="{precision: 0}"\n        />\n    </tui-textfield>\n</p>\n<p class="filters">\n    <label tuiLabel>\n        <input\n            tuiCheckbox\n            type="checkbox"\n            [(ngModel)]="dob"\n        />\n        Enable sorting by DOB\n    </label>\n    <button\n        size="m"\n        tuiButton\n        tuiChevron\n        tuiDropdownOpen\n        type="button"\n        [tuiDropdown]="dropdown"\n    >\n        Columns\n    </button>\n    <ng-template #dropdown>\n        <tui-reorder\n            class="columns"\n            [enabled]="enabled"\n            [(items)]="initial"\n            (enabledChange)="onEnabled($event)"\n        />\n    </ng-template>\n</p>\n<tui-loader\n    [overlay]="true"\n    [showLoader]="!!(loading$ | async)"\n>\n    <table\n        *ngIf="data$ | async as data"\n        tuiTable\n        class="table"\n        [columns]="columns"\n        [direction]="(direction$ | async) || 1"\n        [tuiSortBy]="sorter$ | async"\n        (tuiSortChange)="change($event)"\n    >\n        <thead>\n            <tr tuiThGroup>\n                <th\n                    *tuiHead="\'name\'"\n                    tuiSortable\n                    tuiTh\n                >\n                    Name\n                </th>\n                <th\n                    *tuiHead="\'dob\'"\n                    tuiTh\n                    [tuiSortable]="dob"\n                >\n                    Date of Birth\n                </th>\n                <th\n                    *tuiHead="\'age\'"\n                    tuiSortable\n                    tuiTh\n                    [requiredSort]="true"\n                >\n                    Age\n                </th>\n            </tr>\n        </thead>\n        <tbody\n            *tuiLet="data | tuiTableSort as sortedData"\n            tuiTbody\n            [data]="sortedData"\n        >\n            <tr\n                *ngFor="let item of sortedData"\n                tuiTr\n            >\n                <td\n                    *tuiCell="\'name\'"\n                    tuiTd\n                    [class.match]="isMatch(item.name)"\n                >\n                    {{ item.name }}\n                </td>\n                <td\n                    *tuiCell="\'dob\'"\n                    tuiTd\n                    [class.match]="isMatch(item.dob)"\n                >\n                    {{ item.dob }}\n                </td>\n                <td\n                    *tuiCell="\'age\'"\n                    tuiTd\n                    [class.match]="isMatch(getAge(item))"\n                >\n                    {{ getAge(item) }}\n                </td>\n            </tr>\n        </tbody>\n        <tfoot>\n            <tr>\n                <td [colSpan]="columns.length">\n                    <tui-table-pagination\n                        class="tui-space_top-2"\n                        [page]="(page$ | async) || 0"\n                        [total]="(total$ | async) || 0"\n                        (paginationChange)="onPagination($event)"\n                    />\n                </td>\n            </tr>\n        </tfoot>\n    </table>\n</tui-loader>\n'}}]);