(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[91849],{91849:n=>{n.exports='<h3 tuiTitle>\n    <strong>Truncate</strong>\n    <span tuiSubtitle>\n        Using\n        <code>.text-truncate()</code>\n        mixin\n    </span>\n</h3>\n<tui-breadcrumbs>\n    @for (item of items; track item) {\n        <button\n            *tuiItem\n            tuiHintOverflow\n            tuiLink\n            type="button"\n            class="link"\n            [class.link_last]="$last"\n        >\n            {{ item }}\n        </button>\n    }\n</tui-breadcrumbs>\n<hr />\n<h3 tuiTitle>\n    <strong>Fade</strong>\n    <span tuiSubtitle>\n        Combining\n        <code>.text-truncate()</code>\n        mixin with\n        <code>\n            <a\n                tuiLink\n                [routerLink]="fade"\n            >\n                Fade\n            </a>\n        </code>\n        directive\n    </span>\n</h3>\n<tui-breadcrumbs>\n    @for (item of items; track item) {\n        <button\n            *tuiItem\n            tuiFade\n            tuiHintOverflow\n            tuiLink\n            type="button"\n            class="link"\n            [class.link_last]="$last"\n        >\n            {{ item }}\n        </button>\n    }\n</tui-breadcrumbs>\n<hr />\n<h3 tuiTitle>\n    <strong>Scroll</strong>\n    <span tuiSubtitle>\n        Putting\n        <code>\n            <a\n                tuiLink\n                [routerLink]="fade"\n            >\n                Fade\n            </a>\n        </code>\n        directive on entire component\n    </span>\n</h3>\n<tui-breadcrumbs tuiFade>\n    @for (item of items; track item) {\n        <button\n            *tuiItem\n            tuiLink\n            type="button"\n            [class.link_last]="$last"\n        >\n            {{ item }}\n        </button>\n    }\n</tui-breadcrumbs>\n<hr />\n<h3 tuiTitle>\n    <strong>Collapse</strong>\n    <span tuiSubtitle>\n        Using\n        <code>itemsLimit: number</code>\n    </span>\n</h3>\n<tui-breadcrumbs [itemsLimit]="10">\n    @for (item of items; track item) {\n        <button\n            *tuiItem\n            tuiLink\n            type="button"\n            [class.link_last]="$last"\n        >\n            {{ item }}\n        </button>\n    }\n</tui-breadcrumbs>\n'}}]);